val.onclick:
if( !this.classList.contains('ready') )
            {
                this.classList.add('ready');
                drawsocket.input({
                    key: 'tween',
                    val: [ {
                        id: 'score-anim',
                        cmd : 'play'
                    }, {
                        id: 'miniscore-anim',
                        cmd : 'play'
                    } ],
                    timetag: Date.now()
                });
            } else {
                this.classList.remove('ready');
                let starttime = document.getElementById('userinput');
                drawsocket.input({
                    key: 'tween',
                    val: [{
                        id: 'score-anim',
                        cmd: 'pause',
                        time: starttime.value
                    }, {
                        id: 'miniscore-anim',
                        cmd : 'pause',
                        time: starttime.value
                    }]
                });
            }

val.onkeydown:
if( drawsocket.submitOnEnterKey(this) ) { 
  let button = document.getElementById('playbutton');
  if( button.classList.contains('ready') )
       button.classList.remove('ready');

  drawsocket.input({
    key: 'tween',
    val: [{
      id: 'score-anim',
      cmd: 'pause',
      time: this.value
    }, {
     id: 'miniscore-anim',
  		 cmd : 'pause',
     time: this.value
     }]
  });
}

val.onmousemove:
event.preventDefault();
let x = event.clientX;
if(event.buttons == 1){
    let r = ((x-320) / 700) * 720;
    drawsocket.input({
        key: 'tween',
        val: [{
            id: 'score-anim',
            cmd: 'pause',
            time: r 
        }, {
            id: 'miniscore-anim',
            cmd: 'pause',
            time: r
        }]
    });
    let uiTxt = document.getElementById('userinput');
    uiTxt.value = r;
    let button = document.getElementById('playbutton');
    if( button.classList.contains('ready') )
        button.classList.remove('ready');

}

val.ontouchmove:
event.preventDefault();
let x = event.pageX;
let r = ((x-320) / 700) * 720;
drawsocket.input({
    key: 'tween',
    val: [{
        id: 'score-anim',
        cmd: 'pause',
        time: r 
    }, {
        id: 'miniscore-anim',
        cmd: 'pause',
        time: r
    }]
});
let uiTxt = document.getElementById('userinput');
uiTxt.value = r;

onUpdate.function:

if( this.time() % 1	< 0.05){
  let text = document.getElementById('timecount');
  text.innerHTML = Math.floor( this.time() );
}
