val.onclick:
if( !this.classList.contains('ready') )
{
   this.classList.add('ready');
   drawsocket.input({
     key: 'tween',
     val: [ {
       id: 'score-anim',
  		   cmd : 'play'
     }, {
       id: 'miniscore-anim',
  		   cmd : 'play'
     } ],
     timetag: Date.now()
   });
} else { 
   this.classList.remove('ready');

  let starttime = document.getElementById('userinput');

   drawsocket.input({
     key: 'tween',
     val: [{
       id: 'score-anim',
  		   cmd: 'pause',
       time: starttime.value
     }, {
       id: 'miniscore-anim',
  		   cmd : 'pause',
       time: starttime.value
     }]
   });
}

val.onkeydown:
if( drawsocket.submitOnEnterKey(this) ) { 
  let button = document.getElementById('playbutton');
  if( button.classList.contains('ready') )
       button.classList.remove('ready');

  drawsocket.input({
    key: 'tween',
    val: [{
      id: 'score-anim',
      cmd: 'pause',
      time: this.value
    }, {
     id: 'miniscore-anim',
  		 cmd : 'pause',
     time: this.value
     }]
  });
}

